import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import fire from "../../config/firebase";
import { connect } from "react-redux";

class MenuBar extends Component {
  constructor(props) {
    super(props);
    // console.log("props");
    // console.log(props);
  }

  //   UNSAFE_componentWillReceiveProps(newProps) {
  //     console.log(newProps);
  //   }

  //   componentDidMount() {
  //     this.fetchPageData();
  //   }

  //   fetchPageData() {
  //     fire
  //       .database()
  //       .ref()
  //       .child("pages")
  //       .orderByKey()
  //       .once("value", snapshot => {
  //         let message = snapshot.val();
  //         const msg = Object.keys(message || {}).map(key => ({
  //           ...message[key]
  //         }));
  //         this.setState({ pages: msg });
  //       });
  //   }

  render() {
    var sources = [];
    const loggedInUser = localStorage.getItem("authUser");

    pages.forEach(element => {
      if (element.status === "published") {
        sources.push(element);
      }
    });

    return (
      <div
        className="navbar-collapse"
        style={{
          border: "1px solid gray",
          background: "green",
          borderLeft: "1px solid gray"
        }}
      >
        <ul
          style={{
            listStyleType: "none",
            padding: "12px"
          }}
        >
          <li
            style={{
              display: "inline",
              float: "left"
            }}
            key="Home"
          >
            <NavLink
              style={{
                color: "white",
                padding: "10px",
                textDecoration: "none"
              }}
              to="/"
              exact
              activeClassName="active"
              title="Home"
            >
              <i className="fa fa-home" aria-hidden="true" />
            </NavLink>
          </li>
          {pages.map(page => (
            <span>
              <li
                style={{
                  display: "inline",
                  float: "left",
                  borderLeft: "1px solid gray"
                }}
                key="preview"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "10px",
                    textDecoration: "none"
                  }}
                  to={"/preview/" + page.title}
                  exact
                  activeClassName="active"
                >
                  <span className="glyphicon glyphicon-home" />
                  {page.title}
                </NavLink>
              </li>
            </span>
          ))}

          {loggedInUser ? (
            <div>
              <li
                style={{
                  display: "inline",
                  float: "right"
                }}
                key="/"
              >
                <NavLink
                  onClick={this.logoutHandler}
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/"
                  exact
                  activeClassName="active"
                  title="Logout"
                >
                  <i className="fa fa-sign-out" aria-hidden="true" />
                  {/* Logout */}
                </NavLink>
              </li>

              <li
                style={{
                  display: "inline",
                  float: "right",
                  borderRight: "1px solid gray",
                  borderLeft: "1px solid gray"
                }}
                key="showpages"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/showpages"
                  exact
                  activeClassName="active"
                  title="Manage Pages"
                >
                  <i className="fa fa-gear" aria-hidden="true" /> Manage Pages
                </NavLink>
              </li>
            </div>
          ) : (
            <div>
              <li
                style={{
                  display: "inline",
                  float: "right"
                }}
                key="login"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/login"
                  exact
                  activeClassName="active"
                >
                  Login
                </NavLink>
              </li>
              <li
                style={{
                  display: "inline",
                  float: "right",
                  borderRight: "1px solid gray"
                }}
                key="register"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/register"
                  exact
                  activeClassName="active"
                >
                  Register
                </NavLink>
              </li>
            </div>
          )}
        </ul>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    authState: state.authState,
    pages: Object.keys(state.pageState.pages || {}).map(key => ({
      ...state.pageState.pages[key],
      uid: key
    }))
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onInputChange: e =>
      dispatch({
        type: "onChange",
        name: e.target.name,
        value: e.target.value
      })
    //   ,
    // setIsAuthorised: status => dispatch({ type: "onSetAuthorise", status })
  };
};

export default connect(
  mapStateToProps,
  null
)(MenuBar);
