import React, { Component, Fragment } from "react";
import { NavLink } from "react-router-dom";
import fire from "../../config/firebase";
import { connect } from "react-redux";
import Spinner from "../Spinner";

class MenuBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pages: null,
      loading: true
    };
  }

  componentWillMount() {
    console.log("adaAA");
    this.setState({ loading: true });
    this.fetchPageData();
  }

  componentDidMount() {
    // this.fetchPageData();
    console.log("adaAA3435");
    // document.addEventListener("DOMContentLoaded", function(event) {
    //   var a = true;
    // });
    this.finalCall();
    console.log("adaAA34353");
    // this.setState({ loading: false });
  }

  finalCall = async () => {
    await document.addEventListener("DOMContentLoaded", function(event) {
      return true;
    });
    console.log("adaAA34353234");
    console.log("adaAA34353234qweqe");
    this.setState({ loading: false });
  };

  logoutHandler = () => {
    localStorage.removeItem("authUser");
    this.forceUpdate();
  };

  fetchPageData() {
    fire
      .database()
      .ref()
      .child("pages")
      .orderByKey()
      .once("value", snapshot => {
        let message = snapshot.val();
        const msg = Object.keys(message || {}).map(key => ({
          ...message[key]
        }));
        this.setState({ pages: msg });
      });
  }

  render() {
    const { pages } = this.props.pages.length > 0 ? this.props : this.state;
    if (!pages) {
      return null;
    }
    var sources = [];
    const loggedInUser = localStorage.getItem("authUser");

    pages.forEach(element => {
      if (element.status === "published") {
        sources.push(element);
      }
    });

    var MenuBarPage = (
      <div
        className="navbar-collapse"
        style={{
          border: "1px solid gray",
          background: "green",
          borderLeft: "1px solid gray"
        }}
      >
        <ul
          style={{
            listStyleType: "none",
            padding: "12px"
          }}
        >
          <li
            style={{
              display: "inline",
              float: "left"
            }}
            key="Home"
          >
            <NavLink
              style={{
                color: "white",
                padding: "10px",
                textDecoration: "none"
              }}
              to="/"
              exact
              activeClassName="active"
              title="Home"
            >
              <i className="fa fa-home" aria-hidden="true" />
            </NavLink>
          </li>
          {pages.map(page => (
            <span>
              <li
                style={{
                  display: "inline",
                  float: "left",
                  borderLeft: "1px solid gray"
                }}
                key="preview"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "10px",
                    textDecoration: "none"
                  }}
                  to={"/preview/" + page.title}
                  exact
                  activeClassName="active"
                >
                  <span className="glyphicon glyphicon-home" />
                  {page.title}
                </NavLink>
              </li>
            </span>
          ))}

          {loggedInUser ? (
            <div>
              <li
                style={{
                  display: "inline",
                  float: "right"
                }}
                key="/"
              >
                <NavLink
                  onClick={this.logoutHandler}
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/"
                  exact
                  activeClassName="active"
                  title="Logout"
                >
                  <i className="fa fa-sign-out" aria-hidden="true" />
                  {/* Logout */}
                </NavLink>
              </li>

              <li
                style={{
                  display: "inline",
                  float: "right",
                  borderRight: "1px solid gray",
                  borderLeft: "1px solid gray"
                }}
                key="showpages"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/showpages"
                  exact
                  activeClassName="active"
                  title="Manage Pages"
                >
                  <i className="fa fa-gear" aria-hidden="true" /> Manage Pages
                </NavLink>
              </li>
            </div>
          ) : (
            <div>
              <li
                style={{
                  display: "inline",
                  float: "right"
                }}
                key="login"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/login"
                  exact
                  activeClassName="active"
                >
                  Login
                </NavLink>
              </li>
              <li
                style={{
                  display: "inline",
                  float: "right",
                  borderRight: "1px solid gray"
                }}
                key="register"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/register"
                  exact
                  activeClassName="active"
                >
                  Register
                </NavLink>
              </li>
            </div>
          )}
        </ul>
      </div>
    );

    if (this.state.loading) {
      MenuBarPage = <Spinner />;
    }

    return <Fragment>{MenuBarPage}</Fragment>;
  }
}

const mapStateToProps = state => ({
  pages: Object.keys(state.pageState.pages || {}).map(key => ({
    ...state.pageState.pages[key],
    uid: key
  }))
});

// const mapDispatchToProps = dispatch => ({
//   onSetPages: pages => dispatch({ type: "PAGES_SET", pages })
// });

export default connect(
  mapStateToProps,
  null
)(MenuBar);
