import React, { Component, Fragment } from "react";
import fire from "../config/firebase";
import { Link } from "react-router-dom";
import ChartComponent from "./ChartComponent";
import _ from "lodash";

class CustomPieChart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      auther: [],
      charts: {
        donutChart: {
          paddingAngle: 5,
          innerRadius: 60
        }
      }
    };
  }

  UNSAFE_componentWillMount() {
    let a = [];
    let b = [];
    let abc = {};

    fire
      .database()
      .ref()
      .child("pages")
      .orderByKey()
      .once("value", snapshot => {
        let message = snapshot.val();
        let msg = Object.keys(message || {}).map(key => ({
          name: message[key].author,
          value: 1
        }));
        // console.log(msg);
        let msg1 = Object.keys(message || {}).map(key => ({
          status: message[key].status
        }));

        const mediaTypes = data
          .map(dataItem => dataItem.media_type) // get all media types
          .filter((mediaType, index, array) => array.indexOf(mediaType) === index), // filter out duplicates

          counts = mediaTypes
            .map(mediaType => ({
              type: mediaType,
              count: data.filter(item => item.media_type === mediaType).length
            }));
        // for (let index = 0; index < msg.length; index++) {
        //   if (a[msg[index].name]) {
        //     console.log('1');
        //     msg[index].value = msg[index].value + 1;
        //   }
        //   a[msg[index].name] = msg[index];
        // }
        // console.log(a);
        let count = 0;
        for (abc in a) {
          b[count] = a[abc];
          count++;
        }

        console.log(msg);
        // Auther
        // let auther = _.countBy(msg, function (rec) {
        //   return rec.status === "published";
        // });

        // For category Map
        let category = _.countBy(msg1, function (rec) {
          return rec.status === "published";
        });

        let catgoriesCount = [
          { name: "Published", value: category.true },
          { name: "On Hold", value: category.false }
        ];

        this.setState({ pages: b, category: catgoriesCount });
      });
  }

  render() {
    return (
      <Fragment>
        <Link style={{ float: "right", marginRight: "10px" }} to={"/showpages"}>
          Back
        </Link>
        <br />
        <div className="width-100">
          <div className="container-left">
            <label className="width-100" htmlFor="pieChart">
              <strong>Pie Chart (Author): </strong>
            </label>

            {this.state.pages ? (
              <ChartComponent pages={this.state.pages} />
            ) : null}
          </div>

          <div className="container-right">
            <label className="width-100" htmlFor="pieChart">
              <strong>Donut Chart (Categories): </strong>
            </label>

            <ChartComponent
              pages={this.state.category}
              elementConfig={this.state.charts.donutChart}
            />
          </div>
        </div>
      </Fragment>
    );
  }
}

export default CustomPieChart;
