import React, { Component, Fragment } from "react";
import fire from "./../config/firebase";
import { Link } from "react-router-dom";
import Spinner from "../UI/Spinner";
import { connect } from "react-redux";
import Input from "../UI/Input/Input";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: "",
      loading: false,
      orderForm: {
        email: {
          elementType: "input",
          elementConfig: {
            type: "email",
            placeholder: "Your E-Mail"
          },
          value: ""
        },
        country: {
          elementType: "input",
          elementConfig: {
            type: "text",
            placeholder: "Country"
          },
          value: ""
        }
      }
    };
  }

  onSubmitLogin = e => {
    e.preventDefault();

    this.setState({
      loading: true
    });

    if (!this.props.authState.email || !this.props.authState.password) {
      return this.setState({
        error: { message: "Please enter required details" },
        loading: false
      });
    }

    this.loginAction();
  };

  componentWillMount() {
    this.setState({
      loading: true
    });
  }
  componentDidMount() {
    this.setState({
      loading: false
    });
  }

  validateCredentials() {
    return fire
      .auth()
      .signInWithEmailAndPassword(
        this.props.authState.email,
        this.props.authState.password
      );
  }
  successfullLogin() {
    localStorage.setItem("authUser", this.props.authState.email);
    this.setState({
      loading: false
    });

    if (localStorage.getItem("authUser")) {
      this.props.setIsAuthorised(true);
    }

    this.props.history.push("/");
  }

  loginAction = async () => {
    try {
      await this.validateCredentials();
      await this.successfullLogin();
    } catch (e) {
      this.setState({
        error: e,
        loading: false
      });
    }
  };

  render() {
    const formElementsArray = [];
    for (let key in this.state.orderForm) {
      formElementsArray.push({
        id: key,
        config: this.state.orderForm[key]
      });
    }
    const { error, loading } = this.state;

    // let loginPage = (
    //   <div
    //     style={{
    //       width: "100%",
    //       height: "100%",
    //       borderRadius: "5px",
    //       justifyContent: "center"
    //     }}
    //   >
    //     <form>
    //       <div
    //         style={{
    //           width: "40%",
    //           marginTop: "50px",
    //           background: "gray",
    //           borderRadius: "5px",
    //           alignContent: "center",
    //           justifyContent: "center",
    //           marginLeft: "30%",
    //           color: "white"
    //         }}
    //       >
    //         <br />
    //         <h3>
    //           <strong>Login Page</strong>
    //         </h3>
    //         {error ? (
    //           <div>
    //             <p style={{ color: "red" }}>{error.message}</p>
    //           </div>
    //         ) : null}
    //         <div style={{ marginTop: "20px" }} className="form-group">
    //           <label style={{ width: "20%" }} htmlFor="email">
    //             Email :
    //           </label>
    //           <input
    //             style={{ borderRadius: "5px" }}
    //             type="text"
    //             name="email"
    //             placeholder=" Email"
    //             onChange={this.props.onInputChange}
    //           />
    //         </div>
    //         <div className="form-group">
    //           <label style={{ width: "20%" }} htmlFor="password">
    //             password :
    //           </label>
    //           <input
    //             style={{ borderRadius: "5px" }}
    //             type="text"
    //             name="password"
    //             placeholder=" Password"
    //             // value={this.props.authState.password}
    //             onChange={this.props.onInputChange}
    //           />
    //         </div>
    //         <div className="form-group">
    //           <button
    //             style={{
    //               width: "70px",
    //               borderRadius: "5px",
    //               background: "green",
    //               borderStyle: "none",
    //               padding: "5px",
    //               color: "white"
    //             }}
    //             onClick={this.onSubmitLogin}
    //           >
    //             Login
    //           </button>
    //           <br />
    //           or <br />
    //           Do
    //           <Link to={"/register"}> Register </Link>
    //           here.
    //         </div>
    //         <br />
    //       </div>
    //     </form>
    //   </div>
    // );

    let loginPage = (
      <form onSubmit={this.orderHandler}>
        {formElementsArray.map(formElement => (
          <Input
            key={formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={event => this.inputChangedHandler(event, formElement.id)}
          />
        ))}
        {/* <Button btnType="Success">ORDER</Button> */}
        <button
          style={{
            width: "70px",
            borderRadius: "5px",
            background: "green",
            borderStyle: "none",
            padding: "5px",
            color: "white"
          }}
          onClick={this.onSubmitLogin}
        >
          Login
        </button>
      </form>
    );

    if (loading) {
      loginPage = <Spinner />;
    }

    return <Fragment>{loginPage}</Fragment>;
  }
}

const mapStateToProps = state => {
  return {
    authState: state.authState
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onInputChange: e =>
      dispatch({
        type: "onChange",
        name: e.target.name,
        value: e.target.value
      }),
    setIsAuthorised: status => dispatch({ type: "onSetAuthorise", status })
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Login);
