import React, { Component } from "react";
import { NavLink } from "react-router-dom";

class MenuBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pages: null,
      loading: false
    };
  }

  render() {
    return (
      <div
        className="navbar-collapse"
        style={{
          border: "1px solid gray",
          background: "green",
          borderLeft: "1px solid gray"
        }}
      >
        <ul
          style={{
            listStyleType: "none",
            padding: "12px"
          }}
        >
          <li
            style={{
              display: "inline",
              float: "left"
            }}
            key="Home"
          >
            <NavLink
              style={{
                color: "white",
                padding: "10px",
                textDecoration: "none"
              }}
              to="/"
              exact
              activeClassName="active"
              title="Home"
            >
              <i className="fa fa-home" aria-hidden="true" />
            </NavLink>
          </li>
          {props.pages.map(page => (
            <span>
              <li
                style={{
                  display: "inline",
                  float: "left",
                  borderLeft: "1px solid gray"
                }}
                key="preview"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "10px",
                    textDecoration: "none"
                  }}
                  to={"/preview/" + page.title}
                  exact
                  activeClassName="active"
                >
                  <span className="glyphicon glyphicon-home" />
                  {page.title}
                </NavLink>
              </li>
            </span>
          ))}

          {loggedInUser ? (
            <div>
              <li
                style={{
                  display: "inline",
                  float: "right"
                }}
                key="/"
              >
                <NavLink
                  onClick={this.logoutHandler}
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/"
                  exact
                  activeClassName="active"
                  title="Logout"
                >
                  <i className="fa fa-sign-out" aria-hidden="true" />
                  {/* Logout */}
                </NavLink>
              </li>

              <li
                style={{
                  display: "inline",
                  float: "right",
                  borderRight: "1px solid gray",
                  borderLeft: "1px solid gray"
                }}
                key="showpages"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/showpages"
                  exact
                  activeClassName="active"
                  title="Manage Pages"
                >
                  <i className="fa fa-gear" aria-hidden="true" /> Manage Pages
                </NavLink>
              </li>
            </div>
          ) : (
            <div>
              <li
                style={{
                  display: "inline",
                  float: "right"
                }}
                key="login"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/login"
                  exact
                  activeClassName="active"
                >
                  Login
                </NavLink>
              </li>
              <li
                style={{
                  display: "inline",
                  float: "right",
                  borderRight: "1px solid gray"
                }}
                key="register"
              >
                <NavLink
                  style={{
                    color: "white",
                    padding: "5px",
                    textDecoration: "none"
                  }}
                  to="/register"
                  exact
                  activeClassName="active"
                >
                  Register
                </NavLink>
              </li>
            </div>
          )}
        </ul>
      </div>
    );
  }
}

export default MenuBar;
