import React, { Component, Fragment } from "react";
import fire from "./../config/firebase";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import Spinner from "../UI/Spinner";
import moment from "moment";
import Input from "../UI/Input/Input";
import Validator from "validatorjs";

const initialState = {
  success: "",
  error: "",
  loading: false,
  orderForm: {
    email: {
      elementType: "input",
      elementConfig: {
        type: "email",
        name: "email",
        placeholder: "Username"
      },
      value: "",
      rule: "required|email"
    },
    password: {
      elementType: "input",
      elementConfig: {
        type: "password",
        name: "password",
        placeholder: "Password"
      },
      value: "",
      rule: "required|password"
    },
    fullname: {
      elementType: "input",
      elementConfig: {
        type: "text",
        name: "fullname",
        placeholder: "Full Name"
      },
      value: "",
      rule: "required"
    }
  }
};

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = { ...initialState };
  }

  reset() {
    this.setState(initialState);
  }

  onSubmitRegister = e => {
    e.preventDefault();
    this.setState({
      loading: true
    });

    if (
      // !this.props.registration.email ||
      // !this.props.registration.fullname ||
      // !this.props.registration.password
      !this.state.orderForm.email.value ||
      !this.state.orderForm.fullname.value ||
      !this.state.orderForm.password.value
    ) {
      return this.setState({
        error: { message: "Please enter required details" },
        loading: false
      });
    }

    this.registerAction();
  };

  validateDataWithFireBase() {
    return fire.auth().createUserWithEmailAndPassword(
      // this.props.registration.email,
      // this.props.registration.password
      this.state.orderForm.email.value,
      this.state.orderForm.password.value
    );
  }

  insertToDatabase() {
    // var key = "users/" + this.props.registration.fullname;
    var key = "users/" + this.state.orderForm.fullname.value;
    return fire
      .database()
      .ref(key)
      .set({
        // email: this.props.registration.email,
        // password: this.props.registration.password,
        // fullname: this.props.registration.fullname,
        email: this.state.orderForm.email.value,
        password: this.state.orderForm.password.value,
        fullname: this.state.orderForm.fullname.value,
        created_on: moment().format(),
        updated_on: moment().format()
      });
  }

  registerAction = async () => {
    try {
      await this.validateDataWithFireBase();
      await this.insertToDatabase();
      this.reset();
      this.setState({ success: "User registered successfully" });
    } catch (e) {
      this.setState({
        error: e,
        loading: false
      });
    }
  };

  componentWillMount() {
    this.setState({
      loading: true
    });
  }
  componentDidMount() {
    this.setState({
      loading: false
    });
  }

  inputChangedHandler = (event, inputIdentifier) => {
    const updatedOrderForm = {
      ...this.state.orderForm
    };
    const updatedFormElement = {
      ...updatedOrderForm[inputIdentifier]
    };
    updatedFormElement.value = event.target.value;
    updatedOrderForm[inputIdentifier] = updatedFormElement;
    this.setState({ orderForm: updatedOrderForm });

    let input = { name: "", email: "" };
    let rules = { name: "required", email: "required" };

    let validation = new Validator(input, rules, {
      "required.email": "Without an :attribute we can't reach you!"
    });
    console.log(validation.passes());
    console.log(validation.errors.first("name")); // returns  'The name field is required.'
    console.log(validation.errors.first("email"));
  };

  render() {
    const { success, error, loading } = this.state;

    const formElementsArray = [];
    for (let key in this.state.orderForm) {
      formElementsArray.push({
        id: key,
        config: this.state.orderForm[key]
      });
    }

    // let registrationPage = (
    //   <div
    //     style={{
    //       width: "100%",
    //       height: "100%",
    //       borderRadius: "5px",
    //       justifyContent: "center"
    //     }}
    //   >
    //     <form>
    //       <div
    //         style={{
    //           width: "40%",
    //           marginTop: "50px",
    //           background: "gray",
    //           borderRadius: "5px",
    //           alignContent: "center",
    //           justifyContent: "center",
    //           marginLeft: "30%",
    //           color: "white"
    //         }}
    //       >
    //         <br />
    //         <h3>
    //           <strong>Register Page</strong>
    //         </h3>
    //         {error ? (
    //           <div>
    //             <p style={{ color: "red" }}>{error.message}</p>
    //           </div>
    //         ) : null}

    //         {success ? (
    //           <div>
    //             <p style={{ color: "green" }}>{success}</p>
    //           </div>
    //         ) : null}

    //         {/* <div style={{ marginTop: "20px" }} className="form-group">
    //           <label style={{ width: "20%" }} htmlFor="email">
    //             Email :
    //           </label>
    //           <input
    //             style={{ borderRadius: "5px" }}
    //             type="text"
    //             name="email"
    //             placeholder=" Email"
    //             onChange={this.props.onInputChange}
    //           />
    //         </div>

    //         <div className="form-group">
    //           <label style={{ width: "20%" }} htmlFor="fullname">
    //             Full Name :
    //           </label>
    //           <input
    //             style={{ borderRadius: "5px" }}
    //             type="text"
    //             name="fullname"
    //             placeholder=" Full Name"
    //             onChange={this.props.onInputChange}
    //           />
    //         </div>

    //         <div className="form-group">
    //           <label style={{ width: "20%" }} htmlFor="password">
    //             Password :
    //           </label>
    //           <input
    //             style={{ borderRadius: "5px" }}
    //             type="text"
    //             name="password"
    //             placeholder=" Password"
    //             onChange={this.props.onInputChange}
    //           />
    //         </div> */}

    //         <div className="form-group">
    //           <button
    //             style={{
    //               width: "70px",
    //               borderRadius: "5px",
    //               background: "green",
    //               borderStyle: "none",
    //               padding: "5px",
    //               color: "white"
    //             }}
    //             onClick={this.onSubmitRegister}
    //           >
    //             Register
    //           </button>
    //           <br />
    //           or <br />
    //           <div>
    //             <span>Already registered </span>
    //             <Link
    //               style={{ color: "#f4a941", fontWeight: "bold" }}
    //               to={"/login"}
    //             >
    //               Login
    //             </Link>
    //             <span> from here</span>
    //             <br />
    //           </div>
    //           <br />
    //         </div>
    //       </div>
    //     </form>
    //   </div>
    // );

    let registrationPage = (
      <form>
        <h3>
          <strong>Register Page</strong>
        </h3>
        {error ? (
          <div class="alert alert-danger">
            <strong>Alert!</strong> {error.message}.
          </div>
        ) : null}

        {success ? (
          <div class="alert alert-success">
            <strong>Success!</strong> {success}.
          </div>
        ) : null}

        {formElementsArray.map(formElement => (
          <Fragment>
            <Input
              key={formElement.id}
              label={formElement.id}
              elementType={formElement.config.elementType}
              elementConfig={formElement.config.elementConfig}
              value={formElement.config.value}
              changed={event => this.inputChangedHandler(event, formElement.id)}
            />
            {/* {this.validator.message(
              formElement.id,
              formElement.config.value,
              formElement.config.rule,
              "text-danger"
            )} */}
          </Fragment>
        ))}

        <button className="btn btn-success" onClick={this.onSubmitRegister}>
          Login
        </button>
      </form>
    );

    if (loading) {
      registrationPage = <Spinner />;
    }

    return <Fragment>{registrationPage}</Fragment>;
  }
}

